import React, { useState } from "react";
import { Button } from "react-bootstrap";
import Studymaterial from "./Studymaterial";

function Dashboard() {
  
  // setInterval(function timer(min=60*60){
  //   let minutes=Math.floor(min/60)
  //   let seconds=Math.floor(min%60)
  //   // console.log(seconds)
  //   // console.log(minutes)
  // }, 1000);

  const [show, setShow] = useState(false);
  const clickHandler = () => {

    setShow(!show);

    let myTimer = setInterval(myClock, 1000);
    let c = 3610; //Initially set to 1 hour
    function myClock() {
      --c
      var seconds = c % 60; // Seconds that cannot be written in minutes
      var secondsInMinutes = (c - seconds) / 60; // Gives the seconds that COULD be given in minutes
      var minutes = secondsInMinutes % 60; // Minutes that cannot be written in hours
      var hours = (secondsInMinutes - minutes) / 60;
      // Now in hours, minutes and seconds, you have the time you need.
      console.clear();
      console.log(hours + ":" + minutes + ":" + seconds)
      if (c == 0) {
          clearInterval(myTimer);
      }
  }

  };
  return (
    <div style={{ border: "1px solid gray"}} className="m-3">
      <div className="d-flex justify-content-between m-2">
        <Button
          variant="outline-primary"
          size="lg"
          onClick={() => clickHandler()}
        >
          Study Material 1
        </Button>
        <Button variant="outline-success" size="lg" disabled>
          `Time Remaining ${}`
        </Button>
      </div>
      {show && <Studymaterial close={() => setShow(!show)} />}
      <div className="d-flex justify-content-end m-2">
        <Button variant="outline-success" size="lg" disabled>
          Finish
        </Button>
      </div>
    </div>
  );
}

export default Dashboard;
