import "./App.css";
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";
import login from "../src/Assets/login.svg";
import Axios from "axios";
function App() {
  const [state, setstate] = useState();
  const toggler = () => {
    setstate(!state);
  };

  let [data, setdata] = useState({
    email: "",
    password: "",
    firstname: "",
    "confirm password": "",
    lastname: "",
  });
  const [error, seterror] = useState({
    emailerror: "",
    passworderror: "",
    confirmpassworderror: "",
  });

  const changeHandler = (e) => {
    const value = e.target.value;
    setdata({ ...data, [e.target.name]: value }); //dynamically adding new key value pair to the existing object.//
  };

  const Register = (e) => {
    e.preventDefault();

    if (
      /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(
        data.email
      )
    ) {
      if (/^[A-Za-z]\w{7,14}$/.test(data.password)) {
        if (data.password === data["confirm password"]) {
          let myJSON = JSON.stringify(data);
          Axios.post("http://localhost:9000/app/api/register",myJSON,{
            Headers:{
              Accept: "application/json",
              'Content-Type': 'application/json'
            }
          })
          .then(res=>{
            alert(res.data)
          }).catch(error=>{
            console.log(error)
          })
        } else {
          seterror({
            ...error,
            ["confirmpassworderror"]: "****password does not match****",
          });
        }
      } else {
        seterror({ ...error, ["passworderror"]: "*****Weak password****" });
      }
    } else {
      seterror({ ...error, ["emailerror"]: "****invalid email*****" });
    }
  };

  return (
    <Container fluid className="screenwrapper">
      <Row className="d-flex align-items-center" style={{ height: "100%" }}>
        <Col md={{ span: 6, offset: 3 }}>
          {state ? (
            <Form className="formwrapper">
              <div className="d-flex justify-content-center">
                <img
                  src={login}
                  alt="img"
                  style={{ width: "150px", height: "150px" }}
                />
              </div>
              <Form.Group>
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={data.email}
                  placeholder="Enter email"
                  onChange={changeHandler}
                />
              </Form.Group>
              <p style={{ color: "#ff0303" }}>{error.emailerror}</p>
              <Form.Group>
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  value={data.password}
                  placeholder="Password"
                  onChange={changeHandler}
                />
              </Form.Group>
              <p style={{ color: "#ff0303" }}>{error.passworderror}</p>
              <Form.Group>
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <div className="d-flex justify-content-center">
                <Button className="m-5" variant="primary" type="submit">
                  Login
                </Button>
                <Button className="m-5" variant="primary" onClick={toggler}>
                  Register
                </Button>
              </div>
            </Form>
          ) : (
            <Form className="formwrapper" onSubmit={Register}>
              <div className="d-flex justify-content-center">
                <img
                  src={login}
                  alt="img"
                  style={{ width: "150px", height: "150px" }}
                />
              </div>
              <Form.Group>
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={data.email}
                  placeholder="Enter email"
                  onChange={changeHandler}
                />
              </Form.Group>
              <p style={{ color: "#ff0303" }}>{error.emailerror}</p>
              <Form.Group>
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={data.password}
                  onChange={changeHandler}
                />
              </Form.Group>
              <p style={{ color: "#ff0303" }}>{error.passworderror}</p>
              <Form.Group>
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  name="confirm password"
                  value={data["confirm password"]}
                  onChange={changeHandler}
                />
              </Form.Group>
              <p style={{ color: "#ff0303" }}>{error.confirmpassworderror}</p>
              <Form.Group>
                <Form.Label>First Name</Form.Label>
                <Form.Control
                  type="text"
                  name="firstname"
                  placeholder="First Name"
                  value={data.firstname}
                  onChange={changeHandler}
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Last Name</Form.Label>
                <Form.Control
                  type="text"
                  name="lastname"
                  placeholder="Last Name"
                  value={data.lastname}
                  onChange={changeHandler}
                />
              </Form.Group>
              <Form.Group>
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <div className="d-flex justify-content-center">
                <Button className="m-5" variant="primary" type="submit">
                  Register
                </Button>
                <Button className="m-5" variant="primary" onClick={toggler}>
                  Login
                </Button>
              </div>
            </Form>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
