import "./App.css";
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Form, Button } from "react-bootstrap";
import { Container, Row, Col } from "react-bootstrap";
import login from "../src/Assets/login.svg";

function App() {
  const toggler = () => {
    setstate(!state);
  };
  const Register=(e)=>{
e.preventDefault();
console.log(data)
  }
  const [state, setstate] = useState();
  const values={info: {
    email: "",
    password: "",
    confirm_password: "",
    firstname: "",
    lastname: "",
  }}
  const [data, setdata] = useState(values)
  return (
    <Container fluid className="screenwrapper">
      <Row className="d-flex align-items-center" style={{ height: "100%" }}>
        <Col md={{ span: 6, offset: 3 }}>
          {state ? (
            <Form className="formwrapper">
              <div className="d-flex justify-content-center">
                <img
                  src={login}
                  alt="img"
                  style={{ width: "150px", height: "150px" }}
                />
              </div>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <div className="d-flex justify-content-center">
                <Button className="m-5" variant="primary" type="submit">
                  Login
                </Button>
                <Button className="m-5" variant="primary" onClick={toggler}>
                  Register
                </Button>
              </div>
            </Form>
          ) : (
            <Form className="formwrapper" onSubmit={Register}>
              <div className="d-flex justify-content-center">
                <img
                  src={login}
                  alt="img"
                  style={{ width: "150px", height: "150px" }}
                />
              </div>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Enter email"
                  onChange={(e) => {
                    data.info.email = e.target.value;
                  }}
                />
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    data.info.password = e.target.value;
                  }}
                />
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    data.info.confirm_password = e.target.value;
                  }}
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>First Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="First Name"
                  onChange={(e) => {
                    data.info.firstname = e.target.value;
                  }}
                />
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Last Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Last Name"
                  onChange={(e) => {
                    setdata(data.info.lastname= e.target.value);
                  }}
                />
              </Form.Group>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <div className="d-flex justify-content-center">
                <Button className="m-5" variant="primary" type="submit">
                  Register
                </Button>
                <Button className="m-5" variant="primary" onClick={toggler}>
                  Login
                </Button>
              </div>
            </Form>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
